import { useEffect, useState } from "react";
import { getCategories } from "./api/category";
import { getCourses, getCoursesByCategory } from "./api/course";
import './styles/App.css';

function App() {
  const [categories, setCategories] = useState([]);
  const [courses, setCourses] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [categoryName , setCategoryName] = useState(null);

  useEffect(() => {
    fetchCategories();
    fetchCourses();
  }, []);

  const fetchCategories = async () => {
    const data = await getCategories();
    setCategories(data);
  };

  const fetchCourses = async () => {
    const data = await getCourses();
    setCourses(data);
  };

  const filterCourses = async (id) => {
    const data = await getCoursesByCategory(id);
    setCourses(data);
    setSelectedCategory(id);
  };

  const handleFilterCourses = (id, name) =>{
    filterCourses(id);
    setCategoryName(name);
  }

  return (
    <div className="container mt-4">
      <h1 className="text-center mb-4">Course Catalog</h1>
      <div className="row">
        {/* Sidebar */}
        <div className="col-md-3">
          <h4 className="mb-3">Categories</h4>
          <ul className="list-group">
            <li
                className="list-group-item"
                onClick={() => fetchCourses()}
                style={{ cursor: "pointer" }}
              >
                All
            </li>
            {categories.map((cat) => (
              <li
                key={cat.id}
                className={`list-group-item ${selectedCategory === cat.category_id ? "active" : ""}`}
                onClick={() => handleFilterCourses(cat?.category_id, cat?.name)}
                style={{ cursor: "pointer" }}
              >
                {cat.name} ({cat.courses_count})
              </li>
            ))}
          </ul>
        </div>

        {/* Courses Section */}
        <div className="col-md-9">
          <div className="row">
            {courses.length > 0 ? 
              courses.map((course) => (
                <div key={course.id} className="col-md-4 mb-4">
                  <div className="card">
                    <img src={course.image_preview} className="card-img-top" alt={course.title} />
                    <div className="card-body">
                      <h5 className="card-title">{course.title}</h5>
                      <p className="card-text">{course.description}</p>
                      <span className="badge bg-primary">{course?.category?.name}</span>
                    </div>
                  </div>
                </div>
              ))
              : <div className="card">
                <div className="card-body">
                  <h2 className="card-title">{categoryName} does'nt have any course !</h2>
                </div>
              </div>
            }
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
